import { useState } from "react";
import { Form, Button } from "react-bootstrap";
import PersonalInfo from "./PersonalInfo";
import ValidationCode from "./ValidationCode";

const SignUpWizard = () => {
  const [activeStep, setActiveStep] = useState(0);
  const steps = ["Personal Info", "Validation Code", "Education Info"];

  const handleNext = () => setActiveStep((prevState) => prevState + 1);

  const handleFormSubmit = (e) => {
    e.preventDefault();
    if (activeStep === steps.length - 1) {
      alert("Form Submitted");
    } else {
      handleNext();
    }
  };

  const showActiveStep = () => {
    switch (activeStep) {
      case 0:
        return <PersonalInfo />;
      case 1:
        return <ValidationCode />;
      case 2:
        return <div>To do</div>;
      default:
        throw Error("Unknown step");
    }
  };
  return (
    <Form className="content" onSubmit={handleFormSubmit}>
      {showActiveStep()}

      <Button
        variant="primary"
        disabled={activeStep === 0}
        onClick={() => setActiveStep((prevState) => prevState - 1)}
      >
        {"< Back"}
      </Button>

      <Button variant="primary" type="submit">
        {activeStep === steps.length - 1 ? "Submit" : "Next >"}
      </Button>
    </Form>
  );
};

export default SignUpWizard;
